<?php $term = get_term_by( 'slug', get_query_var('term'), get_query_var('taxonomy') );?><div class="container">	<div class="content-wrapper shop-wrapper row" id="shop-wrapper">		<div class="col-xs-12 col-sm-12 col-md-9">			<?php if(get_the_content()) : ?>			<div class="shop-content"><?php the_content(); ?></div>			<?php endif; ?>			<div class="products-wrap">				<?php 				$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;				$args = array( 'post_type' => 'products', 'paged'=> $paged, 'posts_per_page' => 9 );				if($term){					$args ['tax_query'] = array(						array(						'taxonomy' => 'product_category',			            'field' => 'slug',			            'terms' => $term->slug						));				}				?>				<?php $loop = new WP_Query($args); ?>				<?php if($loop->have_posts()) : ?>					<div class="row infinite-wrap">					<?php while ( $loop->have_posts() ) : $loop->the_post(); ?>						<div class="col-xs-12 col-md-4 infinite-item">							<?php if (has_post_thumbnail() ):								$productImage = wp_get_attachment_image_src( get_post_thumbnail_id( get_the_ID() ), 'medium' ) ;								?>								<div class="product">									<div class="product-thumb" style="background-image: url(<?php echo $productImage[0]; ?>)">									</div><!--end frontpgpicks-item-->									<h4 class="product-title"><?php the_title(); ?></h4>									<a target="_blank" href="<?php the_field('product_url'); ?>" class="btn product-buy-btn">Buy</a>								</div><!--end frontpgpicks-item"-->							<?php endif; ?> 						</div>					<?php endwhile; ?>					</div>				<?php endif; ?>				<?php 						wp_pagenavi(array('query'=>$loop));						wp_reset_postdata(); 					?>			</div>		</div>		<div class="col-xs-12 col-sm-12 col-md-3 shop-sidebar page-sidebar">			<div class="shop-sidebar-title">View Products</div>			<div class="shop-categories">				<ul class="category-links">					<li class="product-cat<?php echo !$term?' active':''; ?>"><a href="<?php echo get_home_url().'/shop'.'#shop-wrapper'; ?>">All</a></li>				<?php					$terms = get_terms( 'product_category', ['hide_empty' => false]);					if (! is_wp_error( $terms ) ){						foreach ($terms as $cat) {							$termLink = get_term_link( $cat );?>							<li class="product-cat<?php echo $term->slug==$cat->slug?' active':'';?>"><a href="<?php echo $termLink.'#shop-wrapper'; ?>"><?php echo $cat->name; ?></a></li>						<?php 						}					}				?>				</ul>			</div>		</div>	</div></div><div class="dwhite-section"></div>